import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  CircularProgress,
  Button,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
} from '@mui/material';
import { dashboardService, testSuiteService } from '../../services/apiService';
import {
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  CheckCircle as CheckCircleIcon,
  PlayArrow as PlayArrowIcon,
  Code as CodeIcon,
  Assessment as AssessmentIcon,
  MergeType as GitMergeIcon,
  AutoAwesome as AutoAwesomeIcon,
  Close as CloseIcon,
  Visibility as VisibilityIcon,
} from '@mui/icons-material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';

// Color configuration for charts
const CHART_COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff7c7c'];

const StatCard: React.FC<{
  title: string;
  value: string | number;
  change?: number;
  icon: React.ReactNode;
  color: string;
  onClick?: () => void;
  clickable?: boolean;
}> = ({ title, value, change, icon, color, onClick, clickable = false }) => (
  <Card 
    sx={{ 
      height: '100%',
      cursor: clickable ? 'pointer' : 'default',
      transition: 'all 0.2s ease-in-out',
      '&:hover': clickable ? {
        transform: 'translateY(-2px)',
        boxShadow: 3,
      } : {}
    }}
    onClick={clickable ? onClick : undefined}
  >
    <CardContent>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
        <Box>
          <Typography color="textSecondary" gutterBottom variant="overline">
            {title}
          </Typography>
          <Typography variant="h4" sx={{ color }}>
            {value}
          </Typography>
          {change !== undefined && (
            <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
              {change >= 0 ? (
                <TrendingUpIcon sx={{ color: 'success.main', mr: 0.5 }} fontSize="small" />
              ) : (
                <TrendingDownIcon sx={{ color: 'error.main', mr: 0.5 }} fontSize="small" />
              )}
              <Typography
                variant="body2"
                sx={{ color: change >= 0 ? 'success.main' : 'error.main' }}
              >
                {Math.abs(change)}%
              </Typography>
            </Box>
          )}
        </Box>
        <Box sx={{ color, opacity: 0.7, display: 'flex', alignItems: 'center', gap: 1 }}>
          {icon}
          {clickable && <VisibilityIcon sx={{ fontSize: 20, opacity: 0.6 }} />}
        </Box>
      </Box>
    </CardContent>
  </Card>
);

const Dashboard: React.FC = () => {
  const [dashboardData, setDashboardData] = useState<any>(null);
  const [testSuites, setTestSuites] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [dataSource, setDataSource] = useState<'api' | 'fallback'>('api');
  const [testDetailsOpen, setTestDetailsOpen] = useState(false);

  // Helper function to calculate test type distribution
  const getTestTypeDistribution = () => {
    const typeCount: { [key: string]: number } = {};
    (testSuites || []).forEach(suite => {
      typeCount[suite.type] = (typeCount[suite.type] || 0) + 1;
    });

    return Object.entries(typeCount).map(([type, count], index) => ({
      name: type,
      value: count,
      color: CHART_COLORS[index % CHART_COLORS.length]
    }));
  };

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        console.log('üöÄ Fetching dashboard data...');
        console.log('üîë Token in localStorage:', !!localStorage.getItem('token'));

        // Fetch dashboard stats and test suites in parallel
        const [statsResponse, suitesResponse] = await Promise.all([
          dashboardService.getStats(),
          testSuiteService.getAll()
        ]);

        console.log('üìä Stats response:', statsResponse);
        console.log('üìù Suites response:', suitesResponse);

        if (statsResponse && statsResponse.success) {
          setDashboardData(statsResponse.data);
          setDataSource('api');
          console.log('‚úÖ Dashboard data set from API:', statsResponse.data);
        } else {
          console.warn('‚ö†Ô∏è Stats response not successful:', statsResponse);
        }

        if (suitesResponse && suitesResponse.success) {
          setTestSuites(suitesResponse.data);
          setDataSource('api');
          console.log('‚úÖ Test suites set from API:', suitesResponse.data);
        } else {
          console.warn('‚ö†Ô∏è Suites response not successful:', suitesResponse);
        }

        // If no data was loaded, set fallback data for demonstration
        if (!statsResponse?.success && !suitesResponse?.success) {
          console.log('üîÑ Loading fallback demo data for display...');
          setDataSource('fallback');
          setDashboardData({
            totalTests: 156,
            passedTests: 142,
            failedTests: 8,
            coverage: 78.5,
            trendsData: [
              { date: "2024-01-01", passed: 120, failed: 5, coverage: 75 },
              { date: "2024-01-02", passed: 135, failed: 7, coverage: 76 },
              { date: "2024-01-03", passed: 142, failed: 8, coverage: 78.5 }
            ]
          });
          setTestSuites([
            { id: 1, name: "User Authentication Tests", type: "UNIT", status: "COMPLETED", coverage: 85 },
            { id: 2, name: "API Integration Tests", type: "INTEGRATION", status: "RUNNING", coverage: 73 },
            { id: 3, name: "E2E Checkout Flow", type: "E2E", status: "PENDING", coverage: 0 }
          ]);
        }
      } catch (err: any) {
        console.error('‚ùå Dashboard fetch error:', err);
        setError(`Failed to load dashboard data: ${err?.message || 'Unknown error'}`);

        // Load fallback data even on error
        console.log('üîÑ Loading fallback data due to error...');
        setDataSource('fallback');
        setDashboardData({
          totalTests: 156,
          passedTests: 142,
          failedTests: 8,
          coverage: 78.5,
          trendsData: [
            { date: "2024-01-01", passed: 120, failed: 5, coverage: 75 },
            { date: "2024-01-02", passed: 135, failed: 7, coverage: 76 },
            { date: "2024-01-03", passed: 142, failed: 8, coverage: 78.5 }
          ]
        });
        setTestSuites([
          { id: 1, name: "User Authentication Tests", type: "UNIT", status: "COMPLETED", coverage: 85 },
          { id: 2, name: "API Integration Tests", type: "INTEGRATION", status: "RUNNING", coverage: 73 },
          { id: 3, name: "E2E Checkout Flow", type: "E2E", status: "PENDING", coverage: 0 }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '200px' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ p: 3 }}>
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  // Calculate additional stats from real data
  const passRate = dashboardData ? 
    ((dashboardData.passedTests / dashboardData.totalTests) * 100).toFixed(1) : '0';

  const runningTests = (testSuites || []).filter(suite => suite.status === 'RUNNING').length;

  const handleRetry = () => {
    setError(null);
    setDataSource('api');
    window.location.reload(); // Simple retry by reloading
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" sx={{ fontWeight: 'bold', mr: 2 }}>
          Dashboard
        </Typography>
        <Chip 
          label={
            dataSource === 'api' ? "üü¢ Live API Data" : 
            dataSource === 'fallback' ? "üü° Demo Data" : 
            "üî¥ No Data"
          } 
          color={
            dataSource === 'api' ? "success" : 
            dataSource === 'fallback' ? "warning" : 
            "error"
          }
          size="small"
        />
      </Box>

      {/* Error Display and Retry */}
      {error && (
        <Alert 
          severity="warning" 
          sx={{ mb: 3 }}
          action={
            <Button color="inherit" size="small" onClick={handleRetry}>
              Retry
            </Button>
          }
        >
          {error} - Showing demo data instead.
        </Alert>
      )}

      {/* Statistics Cards */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Tests"
            value={dashboardData?.totalTests?.toLocaleString() || '0'}
            change={12}
            icon={<CodeIcon sx={{ fontSize: 40 }} />}
            color="#1976d2"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Pass Rate"
            value={`${passRate}%`}
            change={2.1}
            icon={<CheckCircleIcon sx={{ fontSize: 40 }} />}
            color="#2e7d32"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Test Suites"
            value={(testSuites || []).length}
            change={8}
            icon={<AssessmentIcon sx={{ fontSize: 40 }} />}
            color="#ed6c02"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Running Tests"
            value={runningTests}
            icon={<PlayArrowIcon sx={{ fontSize: 40 }} />}
            color="#9c27b0"
          />
        </Grid>
      </Grid>

      {/* PR Metrics Row */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Active PRs"
            value={dashboardData?.activePRs || 0}
            icon={<GitMergeIcon sx={{ fontSize: 40 }} />}
            color="#1976d2"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Merged PRs"
            value={dashboardData?.mergedPRs || 0}
            icon={<CheckCircleIcon sx={{ fontSize: 40 }} />}
            color="#2e7d32"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Test Suites Generated"
            value={dashboardData?.generatedTestSuites || 0}
            icon={<AutoAwesomeIcon sx={{ fontSize: 40 }} />}
            color="#ed6c02"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Coverage"
            value={`${dashboardData?.coverage?.toFixed(1) || 0}%`}
            icon={<TrendingUpIcon sx={{ fontSize: 40 }} />}
            color="#9c27b0"
          />
        </Grid>
      </Grid>

      {/* Charts */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} md={8}>
          <Card>
            <CardContent>
              <Typography variant="h6" sx={{ mb: 2 }}>
                Test Execution Trends
              </Typography>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={dashboardData?.trendsData || []}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Line
                    type="monotone"
                    dataKey="passed"
                    stroke="#2e7d32"
                    strokeWidth={2}
                    dot={{ fill: '#2e7d32' }}
                    name="Passed Tests"
                  />
                  <Line
                    type="monotone"
                    dataKey="failed"
                    stroke="#d32f2f"
                    strokeWidth={2}
                    dot={{ fill: '#d32f2f' }}
                    name="Failed Tests"
                  />
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" sx={{ mb: 2 }}>
                Test Suite Types
              </Typography>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={getTestTypeDistribution()}
                    cx="50%"
                    cy="50%"
                    innerRadius={60}
                    outerRadius={120}
                    paddingAngle={5}
                    dataKey="value"
                  >
                    {getTestTypeDistribution().map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.color} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
              <Box sx={{ mt: 2 }}>
                {getTestTypeDistribution().map((type: any, index: number) => (
                  <Box key={index} sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <Box
                      sx={{
                        width: 12,
                        height: 12,
                        backgroundColor: type.color,
                        borderRadius: '50%',
                        mr: 1,
                      }}
                    />
                    <Typography variant="body2">
                      {type.name}: {type.value}
                    </Typography>
                  </Box>
                ))}
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Recent Activities */}
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" sx={{ mb: 2 }}>
                Test Suites Status
              </Typography>
              <List>
                {(testSuites || []).map((suite) => (
                  <ListItem key={suite.id} divider>
                    <ListItemIcon>
                      <AssessmentIcon 
                        color={suite.status === 'COMPLETED' ? 'success' : 
                               suite.status === 'RUNNING' ? 'primary' : 'action'} 
                      />
                    </ListItemIcon>
                    <ListItemText
                      primary={suite.name}
                      secondary={`Type: ${suite.type} | Coverage: ${suite.coverage}%`}
                    />
                    <Chip
                      size="small"
                      label={suite.status}
                      color={suite.status === 'COMPLETED' ? 'success' : 
                             suite.status === 'RUNNING' ? 'primary' : 'default'}
                      variant="outlined"
                    />
                  </ListItem>
                ))}
              </List>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" sx={{ mb: 2 }}>
                Coverage Trends
              </Typography>
              <ResponsiveContainer width="100%" height={250}>
                <LineChart data={dashboardData?.trendsData || []}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis domain={[60, 100]} />
                  <Tooltip />
                  <Line
                    type="monotone"
                    dataKey="coverage"
                    stroke="#2e7d32"
                    strokeWidth={2}
                    dot={{ fill: '#2e7d32' }}
                    name="Coverage %"
                  />
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Recent PRs Section */}
      {dashboardData?.recentPRs && dashboardData.recentPRs.length > 0 && (
        <Card sx={{ mt: 3 }}>
          <CardContent>
            <Typography variant="h6" sx={{ mb: 2 }}>
              Recent Pull Requests
            </Typography>
            <List>
              {dashboardData.recentPRs.map((pr: any, index: number) => (
                <ListItem
                  key={pr.id}
                  sx={{
                    border: 1,
                    borderColor: 'divider',
                    borderRadius: 1,
                    mb: 1,
                    bgcolor: 'background.paper'
                  }}
                >
                  <ListItemIcon>
                    <GitMergeIcon color={pr.status === 'OPEN' ? 'primary' : 'success'} />
                  </ListItemIcon>
                  <ListItemText
                    primary={
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                          #{pr.number} {pr.title}
                        </Typography>
                        <Chip
                          label={pr.status}
                          size="small"
                          color={pr.status === 'OPEN' ? 'primary' : 'success'}
                        />
                      </Box>
                    }
                    secondary={
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mt: 0.5 }}>
                        <Typography variant="body2" color="text.secondary">
                          Tests: {pr.testsGenerated}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          Pass Rate: {pr.passRate}%
                        </Typography>
                        <Chip
                          label={`${pr.passRate}% Pass Rate`}
                          size="small"
                          color={parseFloat(pr.passRate) >= 90 ? 'success' : parseFloat(pr.passRate) >= 70 ? 'warning' : 'error'}
                        />
                      </Box>
                    }
                  />
                </ListItem>
              ))}
            </List>
          </CardContent>
        </Card>
      )}
    </Box>
  );
};

export default Dashboard;
